mapper.select:
select
a.item_id as itemId,a.silu,a.answer,a.score,i.item_answer as itemAnswer,i.item_content as itemContent,i.item_score as itemScore,i.video_path as videoPath ,i.pic_path as picPath
from answer_info a LEFT JOIN item_info i ON a.item_id=i.id where a.stu_id=#{stuId} and test_id=#{testId}
